import React, { useState } from 'react';

const Home = () => {
    const [url, setUrl] = useState('');
    const [isValid, setIsValid] = useState(true);

    const validateUrl = (value) => {
        const urlPattern = new RegExp('^(https?:\\/\\/)?'+ // protocol
            '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.?)+[a-z]{2,}|'+ // domain name
            '((\\d{1,3}\\.){3}\\d{1,3}))'+ // OR ip (v4) address
            '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*'+ // port and path
            '(\\?[;&a-z\\d%_.~+=-]*)?'+ // query string
            '(\\#[-a-z\\d_]*)?$','i'); // fragment locator
        return !!urlPattern.test(value);
    };

    const handleChange = (e) => {
        const value = e.target.value;
        setUrl(value);
        setIsValid(validateUrl(value));
    };

    const handleFetch = () => {
        if (isValid) {
            // Fetch logic here
            console.log('Fetching data from:', url);
        } else {
            console.log('Invalid URL');
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
            <h1 className="text-4xl font-bold mb-8">Enter site to monitor</h1>
            <div className="w-full max-w-md">
                <input
                    type="text"
                    value={url}
                    onChange={handleChange}
                    className={`w-full p-2 border ${isValid ? 'border-gray-300' : 'border-red-500'} rounded mb-4`}
                    placeholder="Enter URL"
                />
                <button
                    onClick={handleFetch}
                    className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-700"
                >
                    Fetch
                </button>
                {!isValid && <p className="text-red-500 mt-2">Please enter a valid URL.</p>}
            </div>
        </div>
    );
};

export default Home;
