import React, { useState } from 'react';
import axios from 'axios';
import baseurl from '../URL';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Navigate } from 'react-router-dom';

const Signup = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    const [errors, setErrors] = useState({});
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [passwordStrength, setPasswordStrength] = useState('');

    // Toggle password visibility
    const togglePasswordVisibility = () => {
        setPasswordVisible(!passwordVisible);
    };

    // Handle input change
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value
        }));

        validateField(name, value);
    };

    // Password strength evaluation
    const evaluatePasswordStrength = (password) => {
        if (password.length < 6) return 'weak';
        if (!/\d/.test(password) || !/[!@#$%^&*(),.?":{}|<>]/.test(password)) return 'normal';
        if (!/[A-Z]/.test(password) || !/[a-z]/.test(password)) return 'medium';
        return 'strong';
    };

    // Validate input fields
    const validateField = (name, value) => {
        let error = '';

        switch (name) {
            case 'name':
                if (!value.trim()) error = 'Name is required';
                else if (!/^[a-zA-Z\s]*$/.test(value)) error = 'Only letters are allowed';
                break;

            case 'email':
                if (!value.trim()) error = 'Email is required';
                else if (!/\S+@\S+\.\S+/.test(value)) error = 'Invalid email format';
                break;

            case 'password':
                if (!value) {
                    error = 'Password is required';
                    setPasswordStrength('');
                } else {
                    const strength = evaluatePasswordStrength(value);
                    setPasswordStrength(strength);

                    switch (strength) {
                        case 'weak':
                            error = 'Weak: Must be at least 6 characters';
                            break;
                        case 'normal':
                            error = 'Normal: Add a number & special character';
                            break;
                        case 'medium':
                            error = 'Medium: Add both uppercase & lowercase';
                            break;
                        case 'strong':
                            error = 'Strong: Good password!';
                            break;
                        default:
                            break;
                    }
                }
                break;

            case 'confirmPassword':
                if (!value) error = 'Confirm Password is required';
                else if (value !== formData.password) error = 'Passwords do not match';
                break;

            default:
                break;
        }

        setErrors((prevErrors) => ({
            ...prevErrors,
            [name]: error
        }));
    };

    // Validate all fields on submit
    const validateForm = () => {
        let formErrors = {};

        Object.keys(formData).forEach((key) => {
            validateField(key, formData[key]);
        });

        Object.keys(formData).forEach((key) => {
            if (!formData[key].trim()) {
                formErrors[key] = `${key.charAt(0).toUpperCase() + key.slice(1)} is required`;
            }
        });

        setErrors(formErrors);
        return Object.keys(formErrors).length === 0;
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return; 
        }

        if (passwordStrength === 'weak') {
            toast.error('Please enter a stronger password!');
            return;
        }

        try {
            const response = await axios.post(`${baseurl}/register`, {
            name: formData.name,
            email: formData.email,
            password: formData.password
            }, { withCredentials: true });

            toast.success(response.data.message);

            setFormData({ name: '', email: '', password: '', confirmPassword: '' });
            setPasswordStrength('');
            Navigate('/home');
        } catch (error) {
            if (error.response && error.response.data && error.response.data.errors) {
            const serverErrors = error.response.data.errors;
            let formErrors = {};
            serverErrors.forEach(err => {
                formErrors[err.param] = err.msg;
            });
            setErrors(formErrors);
            } else {
            toast.error(error.response?.data?.message || 'Server error');
            }
        }
    };

    // Get color class based on password strength
    const getStrengthColor = () => {
        switch (passwordStrength) {
            case 'weak': return 'text-red-500';
            case 'normal': return 'text-orange-500';
            case 'medium': return 'text-yellow-500';
            case 'strong': return 'text-green-500';
            default: return 'text-red-500';
        }
    };

    return (
        <div className="flex justify-center items-center h-screen bg-gray-100">
            <form className="bg-white p-8 rounded shadow-md w-full max-w-md" onSubmit={handleSubmit}>
                <h2 className="text-2xl font-bold mb-6 text-center text-blue-500">Sign Up</h2>

                {/* Name Field */}
                <div className="mb-4">
                    <label className="block text-gray-700">Name</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                    {errors.name && <p className="text-red-500 text-sm">{errors.name}</p>}
                </div>

                {/* Email Field */}
                <div className="mb-4">
                    <label className="block text-gray-700">Email</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                    {errors.email && <p className="text-red-500 text-sm">{errors.email}</p>}
                </div>

                {/* Password Field */}
                <div className="mb-4 relative">
                    <label className="block text-gray-700">Password</label>
                    <div className="relative">
                        <input
                            type={passwordVisible ? 'text' : 'password'}
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            className="w-full p-2 border border-gray-300 rounded mt-1"
                        />
                        <span
                            className="absolute right-3 top-3 cursor-pointer text-gray-500"
                            onClick={togglePasswordVisibility}
                        >
                            {passwordVisible ? <FaEyeSlash /> : <FaEye />}
                        </span>
                    </div>
                    {errors.password && <p className={`text-sm ${getStrengthColor()}`}>{errors.password}</p>}
                </div>

                {/* Confirm Password Field */}
                <div className="mb-4">
                    <label className="block text-gray-700">Confirm Password</label>
                    <input
                        type="password"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        className="w-full p-2 border border-gray-300 rounded mt-1"
                    />
                    {errors.confirmPassword && <p className="text-red-500 text-sm">{errors.confirmPassword}</p>}
                </div>

                {/* Submit Button */}
                <button
                    type="submit"
                    className={`w-full p-2 rounded transition duration-200 
                        ${passwordStrength === 'weak' ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-500 hover:bg-blue-600 text-white'}
                    `}
                    disabled={passwordStrength === 'weak'}
                >
                    Sign Up
                </button>

                {/* Redirect to Login */}
                <p className="text-center mt-4">
                    Already have an account? <a href="/signin" className="text-blue-500">Log in</a>
                </p>
            </form>
            <ToastContainer />
        </div>
    );
};

export default Signup;
