import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";
import baseUrl from "../URL";

const Navbars = () => {
    const [isOpen, setIsOpen] = useState(false);
    const navigate = useNavigate();
    const [user, setUser] = useState(null); // Store user details instead of just authentication status

    // Check authentication when the component loads
    useEffect(() => {
        const checkAuth = async () => {
            try {
                const response = await axios.get(`${baseUrl}/profile`, { withCredentials: true }); 
                setUser(response.data.user); // Store user details
            } catch (error) {
                setUser(null);
            }
        };
        checkAuth();
    }, []);

    // Logout function
    const handleLogout = async () => {
        try {
            await axios.post(`${baseUrl}/logout`, {}, { withCredentials: true });
            setUser(null); // Reset user state
            toast.success("Logged out successfully!");
            setTimeout(() => {
                navigate("/signIn");
            }, 2000);
        } catch (error) {
            toast.error("Error logging out!");
        }
    };

    return (
        <nav className="bg-white sticky w-full z-10 top-0 start-0 border-b border-gray-200">
            <ToastContainer position="top-right" autoClose={3000} />
            <div className="flex flex-wrap items-center justify-between mx-auto p-4">
                {/* Logo */}
                <Link to="/" className="flex items-center space-x-3">
                    <img
                        src="https://cdn-icons-png.flaticon.com/512/3135/3135768.png"
                        className="h-8"
                        alt="Employee Management Logo"
                    />
                    <span className="self-center font-semibold whitespace-nowrap">
                        {user ? `Welcome, ${user.name}!` : "Please Sign in..."}
                    </span>
                </Link>

                {/* Right Section */}
                <div className="flex md:order-2 space-x-3 md:space-x-2">
                    {!user ? (
                        <>
                            <button
                                type="button"
                                className="text-white bg-blue-500 hover:bg-blue-600 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2"
                                onClick={() => navigate("/signUp")}
                            >
                                Sign Up
                            </button>
                            <button
                                type="button"
                                className="text-white bg-blue-500 hover:bg-blue-600 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2"
                                onClick={() => navigate("/signIn")}
                            >
                                Sign In
                            </button>
                        </>
                    ) : (
                        <>
                            <button
                                type="button"
                                className="text-white bg-blue-500 hover:bg-blue-600 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2"
                                onClick={() => navigate("/home")}
                            >
                                Home
                            </button>
                            <button
                                type="button"
                                className="text-white bg-red-500 hover:bg-red-600 focus:ring-4 focus:outline-none focus:ring-red-300 font-medium rounded-lg text-sm px-4 py-2"
                                onClick={handleLogout}
                            >
                                Log Out
                            </button>
                        </>
                    )}
                </div>

                {/* Mobile Menu Button */}
                <button
                    onClick={() => setIsOpen(!isOpen)}
                    className="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200"
                    aria-controls="navbar-sticky"
                    aria-expanded={isOpen}
                >
                    <span className="sr-only">Open main menu</span>
                    <svg
                        className="w-5 h-5"
                        aria-hidden="true"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 17 14"
                    >
                        <path
                            stroke="currentColor"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"
                            d="M1 1h15M1 7h15M1 13h15"
                        />
                    </svg>
                </button>
            </div>
        </nav>
    );
};

export default Navbars;
